mapper.update:
UPDATE attainment SET attainment_name = #{attainment_name}, attainment_type = #{attainment_type}, attainment_duration = #{attainment_duration}, attainment_target = #{attainment_target}, attainment_finish = #{attainment_finish}, attainment_rate = #{attainment_finish}/#{attainment_target}*100, star = #{star}, start_date = #{start_date},
end_date = null
end_date = #{end_date}
WHERE attainment_no = #{attainment_no}

mapper.select:
SELECT * FROM attainment
WHERE user_id = #{user_id}
AND
reg_date = #{reg_date}
